package com.miaopu.shop.pay;import android.os.Parcel;import android.os.Parcelable;import android.text.TextUtils;import java.util.Map;/** *支付宝支付结果处理 */public class PayResult implements Parcelable {	private String resultStatus;	private String result;	private String memo;	public PayResult(){}	public PayResult(Map<String, String> rawResult) {		if (rawResult == null) {			return;		}		for (String key : rawResult.keySet()) {			if (TextUtils.equals(key, "resultStatus")) {				resultStatus = rawResult.get(key);			} else if (TextUtils.equals(key, "result")) {				result = rawResult.get(key);			} else if (TextUtils.equals(key, "memo")) {				memo = rawResult.get(key);			}		}	}	protected PayResult(Parcel in) {		resultStatus = in.readString();		result = in.readString();		memo = in.readString();	}	@Override	public void writeToParcel(Parcel dest, int flags) {		dest.writeString(resultStatus);		dest.writeString(result);		dest.writeString(memo);	}	@Override	public int describeContents() {		return 0;	}	public static final Creator<PayResult> CREATOR = new Creator<PayResult>() {		@Override		public PayResult createFromParcel(Parcel in) {			return new PayResult(in);		}		@Override		public PayResult[] newArray(int size) {			return new PayResult[size];		}	};	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}	public void setResultStatus(String resultStatus) {		this.resultStatus = resultStatus;	}	public void setResult(String result) {		this.result = result;	}	public void setMemo(String memo) {		this.memo = memo;	}}